/* %if-c-only SNIP! this currently causes conflicts with the c++ scanner */
/* macrolex_destroy is for both reentrant and non-reentrant scanners. */
int macrolex_destroy  (yyscan_t yyscanner)
{
    struct yyguts_t * yyg = (struct yyguts_t*)yyscanner;

    /* Pop the buffer stack, destroying each element. */
    while(YY_CURRENT_BUFFER){
        macro_delete_buffer(YY_CURRENT_BUFFER ,yyscanner );
        YY_CURRENT_BUFFER_LVALUE = NULL;
        macropop_buffer_state(yyscanner);
    }

    /* Destroy the stack itself. */
    macrofree(yyg->yy_buffer_stack ,yyscanner);
    yyg->yy_buffer_stack = NULL;

    /* Destroy the start condition stack. */
        macrofree(yyg->yy_start_stack ,yyscanner );
        yyg->yy_start_stack = NULL;

    /* Reset the globals. This is important in a non-reentrant scanner so the next time
     * macrolex() is called, initialization will occur. */
    yy_init_globals( yyscanner);

/* %if-reentrant */
    /* Destroy the main struct (reentrant only). */
    macrofree ( yyscanner , yyscanner );
    yyscanner = NULL;
/* %endif */
    return 0;
}
