static void
include_file (void* interp, char *file_name)
{
    struct macro_frame_t *frame;
    FILE *file = 0;
    char *ext;

/* XXX: use this code for miniparrot */
#if !defined(_PARROTLIB)
    extern const char* Parrot_imcc_include_paths[];
    STRING *str = Parrot_library_fallback_locate(interp, file_name, Parrot_imcc_include_paths);

    if (str) {
	char* s = string_to_cstring(interp, str);

	file = fopen(s, "r");
	if (file)
	    sourcefile = s;
	else
	    string_cstring_free(s);
    }
#else
    STRING* name = string_from_cstring(interp, file_name, strlen(file_name));
    STRING* result = Parrot_library_query(interp, "include_file_location", name);

    if (result) {
	 /* FIXME: leak */
	sourcefile = strdup(string_to_cstring(interp, result));
	file = fopen( sourcefile, "r" );
    }

#endif

    frame = new_frame();

    if (!file)
        fataly(EX_SOFTWARE, sourcefile, line, strerror(errno));

    ext = strrchr(file_name, '.');
    if (ext) {
        if (strcmp (ext, ".pasm") == 0) {
            pasm_file = 1;
            BEGIN(emit);
        }
        else if (strcmp (ext, ".imc") == 0) {
            pasm_file = 0;
            BEGIN(INITIAL);
        }
    }

    scan_file (frame, file);
}

static void
scan_file (struct macro_frame_t *frame, FILE *file)
{
    frame->buffer = YY_CURRENT_BUFFER;
    frame->next = frames;
    frames = frame;

    /* XXX: Switch the filename */
    line = 1;

    yy_switch_to_buffer(yy_create_buffer(file, YY_BUF_SIZE));
}

void
compile_file(Interp *interp, FILE *file)
{
    YY_BUFFER_STATE buffer;

    buffer = YY_CURRENT_BUFFER;
    yy_switch_to_buffer(yy_create_buffer(file, YY_BUF_SIZE));

#if IMC_TRACE
    fprintf(stderr, "imcc.l: compile_file\n");
#endif

    emit_open(1, interp);
    yyparse((void *) interp);
    imc_compile_all_units(interp);
    imc_cleanup(interp);
    emit_close(interp);

    yy_switch_to_buffer(buffer);
}

void
print_inc(const char *file, int lin)
{
    struct macro_frame_t *f;

    if (frames && frames->is_macro)
        fprintf(stderr, "\nin macro '.%s' line %d\n", file, lin);
    else
        fprintf(stderr, "\nin file '%s' line %d\n", file, lin);
    for (f = frames; f; f = f->next) {
        if (f->file && strcmp(f->file, sourcefile))
            f->next ?
            fprintf(stderr, "\tincluded from '%s' line %d\n",
                f->file, f->line) :
            fprintf(stderr, "\tincluded from '%s' sub '%s' line %d\n",
                f->file, function, f->line);
    }
}

